import React, { useState } from 'react';
import { logEvent } from '../utils/logger';

export default function UrlForm() {
  const [url, setUrl] = useState('');
  const [shortUrl, setShortUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/shorturls', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url })
      });
      const data = await res.json();
      setShortUrl(data.shortLink);
      await logEvent('frontend', 'info', 'component', 'URL submitted successfully');
    } catch (error) {
      await logEvent('frontend', 'error', 'component', error.message);
      alert('Something went wrong.');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter your long URL"
        />
        <button type="submit">Shorten</button>
      </form>
      {shortUrl && <p>Shortened URL: <a href={shortUrl}>{shortUrl}</a></p>}
    </div>
  );
}
